import './Form.css'
import { useState } from 'react'

const Form = () => {
  const [weather, setWeather] = useState({})
  const [cityid, setCity] = useState('');

  const api_key = '9a076a90df603057583eee4e8ca6826b'
  const city = 'bishkek'


   const  submithandlet = (e) => {
    e.preventDefault();
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${api_key}`)
    .then((res) => res.json())
    .then((d) => setWeather(d))
   }


  return (
   <form className='Form-todo' onSubmit={submithandlet}>
    <div className='btn-input'>
    <input className='input-todo' type="text" value={cityi} onChange={(e) => setCity(e.target.value)} placeholder='city...' />
    <button className='btn-submit' type='submit'>search</button>
    </div>
    {JSON.stringify(weather) }
   <span>Город:{weather.name}</span>




   </form>
  )
}

export default Form
