import Card from '../Card';
import Error from '../Error';
import './Form.css'
import { useState } from 'react'

const Form = () => {
  const [weather, setWeather] = useState({})
  const [cityid, setCity] = useState('');c

  const api_key = '9a076a90df603057583eee4e8ca6826b'
  const city = 'bishkek'


   const  submithandlet = (e) => {
    e.preventDefault();
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${api_key}`)
    .then((res) => res.json())
    .then((d) => {
      if(d.name.toLowerCase() === 'bishkek'){
        setWeather(d)
      }else {
        setWeather({});
        alert('dhwjkhdkhdkwhd')
      }

    })
    .catch((error) => {
      console.error('Ошбика получание данных о погоде', error);
      setWeather({})
    })
  }


   const CityInput = (e) => {
    setCity(e.target.value)
   }

  return (
   <form className='Form-todo' onSubmit={submithandlet}>
    <div className='btn-input'>
    <input className='input-todo' type="text" value={cityid} onChange={CityInput } placeholder='city...' />
    <button className='btn-submit' type='submit'>search</button>
    </div>
    <Card weather={weather}/>
    <Error/>

   </form>
  )
}


export default Form
