import Error from '../Error';
import './Form.css'


const Form = ({setWeather, city, setCity, setError}) => {

  const api_key = '9a076a90df603057583eee4e8ca6826b'

   const submithandlet = (e) => {
    e.preventDefault();
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${api_key}`)
    .then((res) => res.json())
    .then(d => {
    if(d.cod && d.cod === '400'){
      setError((prev) => !prev)
    } else {
      setError((prev) => !prev)
    }
    })


  }


   const CityInput = (e) => {
    setCity(e.target.value)
   }

  return (
   <form className='Form-todo' onSubmit={submithandlet}>
    <div className='btn-input'>
    <input className='input-todo' type="text" value={city} onChange={CityInput } placeholder='city...' />
    <button className='btn-submit' type='submit'>search</button>
    </div>

   </form>
  )
}


export default Form
