import React from 'react'

const Weather = ({weather}) => {
    const timeFormater = (sec) => {
        const time = new Date(sec * 1000)
        const hours  = time.getHours()
        const minutes= time.getMinutes()
        const day = time.getDate(); 
        const month = time.getMonth() + 1; 
        return `${hours} : ${minutes}`
    }


    const kelvinToCelsius = (kelvin) => {
        return kelvin - 273.15; 
      };


  return (
    <>
    {
        weather && weather.cod !== '200' ?

        <div className='weather'>
         <h1>{weather.name}, {weather.sys.country}</h1>
         <img src={`http://openweathermap.org/img/w/${weather.weather[0].icon}.png`} />
         <h2>{kelvinToCelsius(weather.main.temp).toFixed(0)}°C</h2>
         <p>{weather.weather[0].description}</p>
         <p>feels_like: {weather.main.feels_like}K</p>
         <p>Ressure: {weather.main.pressure}гПа</p>
         <p>Humidity: {weather.main.humidity}%</p>
         <p>Сloudy: {weather.clouds.all}%</p>
         <p>Wind: {weather.wind.speed}m/s</p>

         <p>sunset: { timeFormater(weather.sys.sunset)}</p>
         <p>Sunrise: { timeFormater(weather.sys.sunrise)}</p>

      </div>
      :
      <></>
    }


    </>
  )
}

