    e.preventDefault();
    console.log('Button clicked');
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${api_key}`)
    .then((res) => res.json())
    .then(data => {
    if(data.cod && data.cod === '400'){

    } else {
      setWeather(data)
    }
    })
  }


   const CityInputHandler = (e) => {
    setCity(e.target.value)
