import './Form.css';
import { useState } from 'react';

const Form = () => {
  const [weather, setWeather] = useState({});
  const [city, setCity] = useState('');

  const api_key = '9a076a90df603057583eee4e8ca6826b';

  const submithandlet = (e) => {
    e.preventDefault();
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${api_key}`)
      .then((res) => res.json())
      .then((data) => setWeather(data));
  };

  return (
    <form className='Form-todo' onSubmit={submithandlet}>
      <div className='btn-input'>
        <input
          className='input-todo'
          type="text"
          placeholder='city...'
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button className='btn-submit' type='submit'>search</button>
      </div>
      <div className='weather-data'>
        <p>Coordinates: {weather.coord ? `lon: ${weather.coord.lon}, lat: ${weather.coord.lat}` : ''}</p>
        <p>Weather: {weather.weather ? weather.weather[0].main : ''}</p>
        <p>Description: {weather.weather ? weather.weather[0].description : ''}</p>
        <p>Temperature: {weather.main ? `${weather.main.temp}K` : ''}</p>
        <p>Feels Like: {weather.main ? `${weather.main.feels_like}K` : ''}</p>
        <p>Pressure: {weather.main ? weather.main.pressure : ''}</p>
        <p>Humidity: {weather.main ? `${weather.main.humidity}%` : ''}</p>
        <p>Wind Speed: {weather.wind ? `${weather.wind.speed} m/s` : ''}</p>
        <p>Cloudiness: {weather.clouds ? `${weather.clouds.all}%` : ''}</p>
        <p>Sunrise: {weather.sys ? new Date(weather.sys.sunrise * 1000).toLocaleTimeString() : ''}</p>
        <p>Sunset: {weather.sys ? new Date(weather.sys.sunset * 1000).toLocaleTimeString() : ''}</p>
      </div>
    </form>
  );
};

export default Form;
