import './Form.css'
import { useState } from 'react'

const Form = () => {
  const [weather, setWeather] = useState({})
  const [cityid, setCity] = useState('');

  const api_key = '9a076a90df603057583eee4e8ca6826b'
  const city = 'bishkek'


   const  submithandlet = (e) => {
    e.preventDefault();
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${api_key}`)
    .then((res) => res.json())
  .then((data) => {
    // Проверяем, что данные о погоде возвращены и имеют свойство 'name'
    if (data && data.name && data.name.toLowerCase() === 'bishkek') {
      setWeather(data);
    } else {
      setWeather({});
      alert('Пожалуйста, введите город Bishkek');
    }
  })
  .catch((error) => {
    console.error('Ошибка получения данных о погоде:', error);
    setWeather({});
  });
  }

  return (
   <form className='Form-todo' onSubmit={submithandlet}>
    <div className='btn-input'>
    <input className='input-todo' type="text" value={cityid} onChange={(e) => setCity(e.target.value)} placeholder='city...' />
    <button className='btn-submit' type='submit'>search</button>
    </div>

    <div className='weather-data'>
    {Object.keys(weather).length > 0 ? (
          <div>
              <h1>{weather.name}, {weather.sys.country}</h1>
            <p>lon: {weather.coord.lon},</p>
            <p>lat:{weather.coord.lat}</p>
            <p>{weather.weather[0].description}</p>
            <p>Температура: {weather.main.temp}</p>
            <p>По ощущениям: {weather.main.feels_like}K</p>
            <p>Давление: {weather.main.pressure}</p>
            <p>Влажность: {weather.main.humidity}%</p>
            <p>Скорость ветра: {weather.wind.speed}m/s</p>
            <p>Облачность: {weather.clouds.all}%</p>
            <p>Восход: {new Date(weather.sys.sunrise * 1000).toLocaleTimeString()}</p>
            <p>Закать: {new Date(weather.sys.sunset * 1000).toLocaleTimeString()}</p>
          </div>
        ) : null}

{/* <pre>{JSON.stringify(weather, null, 2)}</pre> */}
      </div>




   </form>
  )
}

export default Form
